import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Dashboard.css';

const vehicles = [
  { id: 1, name: 'Toyota Camry', mileage: 32000, oilChangeDue: true },
  { id: 2, name: 'Honda Accord', mileage: 27000, oilChangeDue: false },
  { id: 3, name: 'Ford F-150', mileage: 45000, oilChangeDue: true },
];

function Dashboard() {
  const [showNotification, setShowNotification] = useState(true);

  const handleRemindMe = () => {
    setShowNotification(false);
    // Add your "remind me" logic here
  };

  const handleServiceDone = () => {
    setShowNotification(false);
    // Add your "service done" logic here
  };

  return (
    <div className="dashboard">
      {showNotification && (
        <div className="custom-popup-overlay">
          <div className="custom-popup">
            <h1 className="popup-title">NOTIFICATION</h1>
            <p className="popup-message">TIRE ROTATION AND BRAKE SERVICE DUE IN</p>
            <div className="popup-distance">900 KM</div>
            <div className="popup-buttons">
              <button className="popup-btn" onClick={handleRemindMe}>REMIND ME</button>
              <button className="popup-btn" onClick={handleServiceDone}>SERVICE DONE</button>
            </div>
          </div>
        </div>
      )}
      <h1>Vehicle Overview</h1>
      <div className="vehicle-list">
        {vehicles.map((vehicle) => (
          <Link key={vehicle.id} to={`/vehicle/${vehicle.id}`} className="vehicle-card-link">
            <div className="vehicle-card">
              <h3>{vehicle.name}</h3>
              <p>Mileage: {vehicle.mileage.toLocaleString()} km</p>
              <p>
                Oil Change Due:{' '}
                <strong style={{ color: vehicle.oilChangeDue ? 'red' : 'green' }}>
                  {vehicle.oilChangeDue ? 'Yes' : 'No'}
                </strong>
              </p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
