import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import './Dashboard.css';

function Dashboard() {
  const [vehicles, setVehicles] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const navigate = useNavigate();

  // Check login state on mount and fetch vehicles
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    try {
      const decoded = jwtDecode(token);
      const now = Date.now() / 1000;
      if (decoded.exp < now) {
        localStorage.removeItem('token');
        localStorage.removeItem('isLoggedIn');
        navigate('/login');
        return;
      }
      // Fetch vehicles for this user
      fetch('http://localhost:5000/api/vehicles', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => {
          setVehicles(data);

          // Notification logic for every vehicle
          const notifs = [];
          data.forEach(vehicle => {
            const last_service_km = Number(vehicle.last_service_km);
            const service_interval_km = Number(vehicle.service_interval_km);
            const current_km = Number(vehicle.current_mileage);

            // Only proceed if both values are positive and not NaN
            if (
              !isNaN(last_service_km) && last_service_km > 0 &&
              !isNaN(service_interval_km) && service_interval_km > 0
            ) {
              const next_service_km = last_service_km + service_interval_km;
              const diff = current_km - next_service_km;
              if (
                !isNaN(current_km) &&
                ((diff > 0 && diff <= 200) || (diff < 0 && diff >= -200))
              ) {
                notifs.push({
                  id: vehicle.id,
                  name: vehicle.name,
                  diff,
                  message: diff > 0
                    ? `Reminder: Servis kendaraan ${vehicle.name} sudah lewat!`
                    : `Reminder: Servis kendaraan ${vehicle.name} dalam ${Math.abs(diff)} KM!`,
                  distance: Math.abs(diff)
                });
              }
            }
          });
          setNotifications(notifs);
        })
        .catch(() => setVehicles([]));
    } catch (err) {
      localStorage.removeItem('token');
      localStorage.removeItem('isLoggedIn');
      navigate('/login');
    }
  }, [navigate]);

  const handleRemindMe = (id) => {
    setNotifications(notifications.filter(n => n.id !== id));
    // Add your "remind me" logic here
  };

  const handleServiceDone = (id) => {
    setNotifications(notifications.filter(n => n.id !== id));
    // Add your "service done" logic here
  };

  return (
    <div className="dashboard">
      {notifications.map((notif) => (
        <div className="custom-popup-overlay" key={notif.id}>
          <div className="custom-popup">
            <h1 className="popup-title">NOTIFICATION</h1>
            <p className="popup-message">{notif.message}</p>
            <div className="popup-distance">
              {notif.distance !== null ? `${notif.distance} KM` : ''}
            </div>
            <div className="popup-buttons">
              <button className="popup-btn" onClick={() => handleRemindMe(notif.id)}>REMIND ME</button>
              <button className="popup-btn" onClick={() => handleServiceDone(notif.id)}>SERVICE DONE</button>
            </div>
          </div>
        </div>
      ))}
      <h1>Vehicle Overview</h1>
      <div className="vehicle-list">
        {vehicles.map((vehicle) => (
          <Link key={vehicle.id} to={`/vehicle/${vehicle.id}`} className="vehicle-card-link">
            <div className="vehicle-card">
              <h3>{vehicle.name}</h3>
              <p>Mileage: {vehicle.current_mileage?.toLocaleString()} km</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
