import React, { useState } from 'react';
import './AddVehicle.css'; // Optional styling

const AddVehicle = () => {
  const [vehicle, setVehicle] = useState({
    name: '',
    mileage: '',
    oilChangeDue: false
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setVehicle((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // You can replace this with an API call to your backend
    console.log('Vehicle submitted:', vehicle);
    setMessage('Vehicle added successfully!');
    setVehicle({ name: '', mileage: '', oilChangeDue: false });

    // Optionally redirect or refresh dashboard
    // navigate('/');
  };

  return (
    <div className="add-vehicle-container">
      <form onSubmit={handleSubmit} className="add-vehicle-form">
        <h2>Add Vehicle</h2>
        {message && <p className="success-message">{message}</p>}

        <input
          type="text"
          name="name"
          placeholder="Vehicle Name"
          value={vehicle.name}
          onChange={handleChange}
          required
        />

        <input
          type="number"
          name="mileage"
          placeholder="Mileage (km)"
          value={vehicle.mileage}
          onChange={handleChange}
          required
        />

        <label>
          <input
            type="checkbox"
            name="oilChangeDue"
            checked={vehicle.oilChangeDue}
            onChange={handleChange}
          />
          Oil Change Due
        </label>

        <button type="submit">Add Vehicle</button>
      </form>
    </div>
  );
};

export default AddVehicle;
