import React, { useState } from 'react';
import './AddVehicle.css';

const AddVehicle = () => {
  const [form, setForm] = useState({
    name: '',
    owner: '',
    mileage: '',
    oilChangeDue: '',
    transmissionOilDue: '',
    brakeServiceDue: '',
    tireRotationDue: '',
    alignmentDue: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/vehicles', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      const data = await response.json();
      if (response.ok) {
        alert('Vehicle registered!');
        setForm({
          name: '',
          owner: '',
          mileage: '',
          oilChangeDue: '',
          transmissionOilDue: '',
          brakeServiceDue: '',
          tireRotationDue: '',
          alignmentDue: ''
        });
      } else {
        alert(data.message || 'Failed to register vehicle.');
      }
    } catch (err) {
      alert('Server error. Please try again later.');
    }
  };

  return (
    <div className="vehicle-form-container">
      <form className="vehicle-form" onSubmit={handleSubmit}>
        <h2>ADD VEHICLE DATA</h2>

        <div className="form-row">
          <input type="text" name="name" placeholder="Name" value={form.name} onChange={handleChange} required />
          <input type="text" name="owner" placeholder="Vehicle Maintenance Due" value={form.owner} onChange={handleChange} required />
        </div>

        <div className="form-row">
          <input type="number" name="mileage" placeholder="Current Mileage" value={form.mileage} onChange={handleChange} required />
          <input type="number" name="brakeServiceDue" placeholder="Brake Service Due" value={form.brakeServiceDue} onChange={handleChange} />
        </div>

        <div className="form-row">
          <input type="number" name="oilChangeDue" placeholder="Oil Change Due" value={form.oilChangeDue} onChange={handleChange} />
          <input type="number" name="tireRotationDue" placeholder="Tire Rotation Due" value={form.tireRotationDue} onChange={handleChange} />
        </div>

        <div className="form-row">
          <input type="number" name="transmissionOilDue" placeholder="Transmission Oil Due" value={form.transmissionOilDue} onChange={handleChange} />
          <input type="number" name="alignmentDue" placeholder="Vehicle Alignment Due" value={form.alignmentDue} onChange={handleChange} />
        </div>

        <button type="submit" className="register-btn">REGISTER NOW</button>
      </form>
    </div>
  );
};

export default AddVehicle;
