import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import Dashboard from './components/Dashboard';
import VehicleDetail from './components/VehicleDetail';
import Login from './components/Login';
import Header from './components/Header';
import Register from './components/Register';
import AddVehicle from './components/AddVehicle';
import EditVehicle from './components/EditVehicle';
import './App.css';

const LayoutWrapper = () => {
  const location = useLocation();
  const isLoginPage = location.pathname === '/login';
  const isRegisterPage = location.pathname === '/register';
  const isLoggedIn = localStorage.getItem('isLoggedIn');

  // Redirect to /login if not logged in and not already on login/register page
  if (!isLoggedIn && !isLoginPage && !isRegisterPage) {
    return <Navigate to="/login" replace />;
  }

  if (isLoginPage) {
    return (
      <div className="app-container">
        <div className="main-content">
          <Routes>
            <Route path="/login" element={<Login />} />
          </Routes>
        </div>
      </div>
    );
  }

  if (isRegisterPage) {
    return (
    <div className="app-container">
    <div className="main-content">
      <Routes>
        <Route path="/register" element={<Register />} />
      </Routes>
    </div>
    </div>
    );
  }

  return (
    <div className="app-container">
      <Sidebar />
      <div className="main-content">
        <Header />
        <Routes>
          <Route path="/" element={<Dashboard />} />
          <Route path="/vehicle/:id" element={<VehicleDetail />} />
          <Route path="/Vehicle-add" element={<AddVehicle />} />
          <Route path="/Vehicle-detail" element={<VehicleDetail />} />
          <Route path="/vehicle/:id/edit" element={<EditVehicle />} />
        </Routes>
      </div>
    </div>
  );
};

function App() {
  useEffect(() => {
    // Clear tokens and login flags on first load
    localStorage.removeItem('token');
    localStorage.removeItem('isLoggedIn');
  }, []);

  return (
    <Router>
      <LayoutWrapper />
    </Router>
  );
}

export default App;
