import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Sidebar.css';
import VirtualAIMechanicChat from './VirtualAIMechanicChat';

function Sidebar() {
  const [aiChatOpen, setAiChatOpen] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('isLoggedIn');
    navigate('/login');
  };

  return (
    <div className="sidebar">
      <h2 style={{ textAlign: 'center', width: '100%' }}>Virtual garage</h2>
      <nav>
        <ul>
          <li>
            <Link to="/vehicle-add">Add Vehicle</Link>
          </li>
          <li>
            <button onClick={() => setAiChatOpen(true)} style={{ width: '100%', background: 'none', border: 'none', color: '#007bff', textAlign: 'left', padding: 0 }}>
              Virtual Ai Mechanic
            </button>
          </li>
          <li>
            <Link to="/self-diagnosis">Self Diagnosis</Link>
          </li>
        </ul>
      </nav>
      <button className="logout-btn" onClick={handleLogout}>Logout</button>
      <VirtualAIMechanicChat open={aiChatOpen} onClose={() => setAiChatOpen(false)} />
    </div>
  );
}

export default Sidebar;
