import React, { useState } from 'react';

function VirtualAIMechanicChat({ open, onClose }) {
  const [messages, setMessages] = useState([
    { from: 'ai', text: 'Halo! Saya Virtual AI Mechanic. Ada yang bisa saya bantu tentang kendaraan Anda?' }
  ]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (!input.trim()) return;
    setMessages([...messages, { from: 'user', text: input }]);
    // Dummy AI response (replace with real AI API call if needed)
    setTimeout(() => {
      setMessages(msgs => [
        ...msgs,
        { from: 'ai', text: 'Terima kasih atas pertanyaannya. Fitur AI akan segera tersedia!' }
      ]);
    }, 800);
    setInput('');
  };

  if (!open) return null;

  return (
    <div className="ai-chat-modal-overlay" style={{
      position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.4)', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000
    }}>
      <div className="ai-chat-modal" style={{
        background: '#fff', borderRadius: 12, width: 350, maxWidth: '90vw', padding: 20, boxShadow: '0 2px 16px #0002'
      }}>
        <h3 style={{ marginTop: 0 }}>Virtual AI Mechanic</h3>
        <div style={{
          minHeight: 120, maxHeight: 220, overflowY: 'auto', marginBottom: 12, background: '#f7f7f7', borderRadius: 8, padding: 8
        }}>
          {messages.map((msg, idx) => (
            <div key={idx} style={{
              textAlign: msg.from === 'ai' ? 'left' : 'right',
              margin: '6px 0'
            }}>
              <span style={{
                background: msg.from === 'ai' ? '#e0e0e0' : '#cce5ff',
                borderRadius: 8,
                padding: '6px 12px',
                display: 'inline-block',
                maxWidth: '80%'
              }}>
                {msg.text}
              </span>
            </div>
          ))}
        </div>
        <div style={{ display: 'flex', gap: 8 }}>
          <input
            style={{ flex: 1, borderRadius: 6, border: '1px solid #ccc', padding: 6 }}
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && handleSend()}
            placeholder="Tulis pertanyaan Anda..."
          />
          <button onClick={handleSend} style={{ borderRadius: 6, padding: '6px 16px' }}>Kirim</button>
        </div>
        <button onClick={onClose} style={{ marginTop: 12, float: 'right', background: '#eee', border: 'none', borderRadius: 6, padding: '6px 16px' }}>Tutup</button>
      </div>
    </div>
  );
}

export default VirtualAIMechanicChat;
