import React from 'react';
import { Link } from 'react-router-dom'; // Import Link
import './Sidebar.css';



const handleLogout = () => {
  localStorage.removeItem('isLoggedIn');
  navigate('/login');
};


function Sidebar() {
  return (
    <div className="sidebar">
    <h2 style={{ textAlign: 'center', width: '100%' }}>Virtual garage</h2>
      <nav>
        <ul>
        <Link to="/vehicle-add">Add Vehicle</Link> {/* Wrap with Link */}

          <li>Virtual Ai Mechanic</li>
          <li>Vehicle Overview</li>
        </ul>
      </nav>
      <button className="logout-btn">Logout</button>
    </div>
  );
}

export default Sidebar;
