import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./VehicleDetail.css"; // Style in separate CSS file

function VehicleDetail() {
  const { id } = useParams();
  const [vehicle, setVehicle] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }
    fetch(`http://localhost:5000/api/vehicles/${id}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => setVehicle(data))
      .catch(() => setVehicle(null));
  }, [id, navigate]);

  if (!vehicle) return <div>Loading...</div>;

  return (
    <div className="vehicle-detail-container">
      <h1 className="title">Vehicle Detail</h1>
      <div className="form-grid">
        <div className="field">
          <label>Name</label>
          <input value={vehicle.name} readOnly />
        </div>
        <div className="field">
          <label>Vehicle Maintenance Due</label>
          <input value={vehicle.maintenanceDue} readOnly />
        </div>

        <div className="field">
          <label>
            <i>Current Mileage</i>
          </label>
          <input value={vehicle.currentMileage} readOnly />
        </div>
        <div className="field">
          <label>Brake Service Due</label>
          <input value={vehicle.brakeServiceDue} readOnly />
        </div>

        <div className="field">
          <label>Oil Change Due</label>
          <input value={vehicle.oilChangeDue} readOnly />
        </div>
        <div className="field">
          <label>Tire Rotation Due</label>
          <input value={vehicle.tireRotationDue} readOnly />
        </div>

        <div className="field">
          <label>Transmission Oil Due</label>
          <input value={vehicle.transmissionOilDue} readOnly />
        </div>
        <div className="field">
          <label>Vehicle Alignment Due</label>
          <input value={vehicle.alignmentDue} readOnly />
        </div>
      </div>

      <button className="back-button" onClick={() => navigate(-1)}>
        Back to Dashboard
      </button>
    </div>
  );
}

export default VehicleDetail;
