import React, { useState } from 'react';

function SelfDiagnosis() {
  const [answer, setAnswer] = useState(null);

  return (
    <div className="self-diagnosis-container" style={{ maxWidth: 500, margin: '40px auto', padding: 24, background: '#fff', borderRadius: 12 }}>
      <h2>Self Diagnosis</h2>
      <div style={{ marginBottom: 24 }}>
        <strong>1) Apakah kendaraan Anda masih bisa menyala dan beroperasi?</strong>
      </div>
      <div style={{ marginBottom: 24 }}>
        <button
          style={{ marginRight: 16, padding: '8px 24px' }}
          onClick={() => setAnswer('no')}
        >
          Tidak
        </button>
        <button
          style={{ padding: '8px 24px' }}
          onClick={() => setAnswer('yes')}
        >
          Ya
        </button>
      </div>
      {answer === 'no' && (
        <div style={{ color: 'red', fontWeight: 'bold' }}>
          Segera hubungi bengkel terdekat karena kemungkinan memiliki masalah serius.<br />
          Silahkan konsultasikan pada mekanik dan kemungkinan aki anda lemah.
        </div>
      )}
      {answer === 'yes' && (
        <div style={{ color: 'green', fontWeight: 'bold' }}>
          Kemungkinan kendaraan anda masih baik-baik saja.
        </div>
      )}
    </div>
  );
}

export default SelfDiagnosis;
