import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

function EditVehicle() {
  const { id } = useParams();
  const [vehicle, setVehicle] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    fetch(`http://localhost:5000/api/vehicles/${id}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => {
        if (!res.ok) throw new Error("Vehicle not found");
        return res.json();
      })
      .then((data) => setVehicle(data))
      .catch((err) => setError(err.message));
  }, [id]);

  const handleChange = (e) => {
    setVehicle({ ...vehicle, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    fetch(`http://localhost:5000/api/vehicles/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(vehicle),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Failed to update vehicle");
        return res.json();
      })
      .then(() => navigate(`/vehicle/${id}`))
      .catch((err) => setError(err.message));
  };

  if (error) return <div>{error}</div>;
  if (!vehicle) return <div>Loading...</div>;

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 900, margin: "40px auto" }}>
      <h1>Edit Vehicle</h1>
      <label>Name</label>
      <input name="name" value={vehicle.name || ""} onChange={handleChange} />
      <label>Current Mileage</label>
      <input name="current_mileage" value={vehicle.current_mileage || ""} onChange={handleChange} />
      <label>Last Service KM</label>
      <input name="last_service_km" value={vehicle.last_service_km || ""} onChange={handleChange} />
      <label>Service Interval KM</label>
      <input name="service_interval_km" value={vehicle.service_interval_km || ""} onChange={handleChange} />
      <label>Pajak Due</label>
      <input type="date" name="pajak_due" value={vehicle.pajak_due ? vehicle.pajak_due.slice(0,10) : ""} onChange={handleChange} />
      <button type="submit">Save</button>
    </form>
  );
}

export default EditVehicle;
