import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import './Dashboard.css';

function Dashboard() {
  const [vehicles, setVehicles] = useState([]);
  const [showNotification, setShowNotification] = useState(false);
  const [notificationMsg, setNotificationMsg] = useState('');
  const [notificationDistance, setNotificationDistance] = useState(null);
  const navigate = useNavigate();

  // Check login state on mount and fetch vehicles
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    try {
      const decoded = jwtDecode(token);
      const now = Date.now() / 1000;
      if (decoded.exp < now) {
        localStorage.removeItem('token');
        localStorage.removeItem('isLoggedIn');
        navigate('/login');
        return;
      }
      // Fetch vehicles for this user
      fetch('http://localhost:5000/api/vehicles', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => {
          setVehicles(data);

          // Notification logic
          let found = false;
          data.forEach(vehicle => {
            const last_service_km = Number(vehicle.last_service_km);
            const service_interval_km = Number(vehicle.service_interval_km);
            const current_km = Number(vehicle.current_mileage);

            // Only proceed if both values are positive and not NaN
            if (
              !isNaN(last_service_km) && last_service_km > 0 &&
              !isNaN(service_interval_km) && service_interval_km > 0
            ) {
              const next_service_km = last_service_km + service_interval_km;
              const diff = current_km - next_service_km;
              if (
                !isNaN(current_km) &&
                diff >= -200 && diff <= 200
              ) {
                setShowNotification(true);
                if (diff > 0) {
                  setNotificationMsg(`Reminder: Servis kendaraan ${vehicle.name} sudah lewat!`);
                  setNotificationDistance(diff);
                } else {
                  setNotificationMsg(`Reminder: Servis kendaraan ${vehicle.name} dalam ${(diff)} KM!`);
                }
                found = true;
              }
            }
          });
          if (!found) {
            setShowNotification(false);
          }
        })
        .catch(() => setVehicles([]));
    } catch (err) {
      localStorage.removeItem('token');
      localStorage.removeItem('isLoggedIn');
      navigate('/login');
    }
  }, [navigate]);

  const handleRemindMe = () => {
    setShowNotification(false);
    // Add your "remind me" logic here
  };

  const handleServiceDone = () => {
    setShowNotification(false);
    // Add your "service done" logic here
  };

  return (
    <div className="dashboard">
      {showNotification && (
        <div className="custom-popup-overlay">
          <div className="custom-popup">
            <h1 className="popup-title">NOTIFICATION</h1>
            <p className="popup-message">{notificationMsg}</p>
            <div className="popup-distance">
              {notificationDistance !== null ? `${notificationDistance} KM` : ''}
            </div>
            <div className="popup-buttons">
              <button className="popup-btn" onClick={handleRemindMe}>REMIND ME</button>
              <button className="popup-btn" onClick={handleServiceDone}>SERVICE DONE</button>
            </div>
          </div>
        </div>
      )}
      <h1>Vehicle Overview</h1>
      <div className="vehicle-list">
        {vehicles.map((vehicle) => (
          <Link key={vehicle.id} to={`/vehicle/${vehicle.id}`} className="vehicle-card-link">
            <div className="vehicle-card">
              <h3>{vehicle.name}</h3>
              <p>Mileage: {vehicle.current_mileage?.toLocaleString()} km</p>
              {/* Remove oil_change_due and other removed fields */}
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
