import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import './Dashboard.css';

function Dashboard() {
  const [vehicles, setVehicles] = useState([]);
  const [showNotification, setShowNotification] = useState(true);
  const navigate = useNavigate();

  // Check login state on mount and fetch vehicles
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    try {
      const decoded = jwtDecode(token);
      const now = Date.now() / 1000;
      if (decoded.exp < now) {
        localStorage.removeItem('token');
        localStorage.removeItem('isLoggedIn');
        navigate('/login');
        return;
      }
      // Fetch vehicles for this user
      fetch('http://localhost:5000/api/vehicles', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => setVehicles(data))
        .catch(() => setVehicles([]));
    } catch (err) {
      localStorage.removeItem('token');
      localStorage.removeItem('isLoggedIn');
      navigate('/login');
    }
  }, [navigate]);

  const handleRemindMe = () => {
    setShowNotification(false);
    // Add your "remind me" logic here
  };

  const handleServiceDone = () => {
    setShowNotification(false);
    // Add your "service done" logic here
  };

  return (
    <div className="dashboard">
      {showNotification && (
        <div className="custom-popup-overlay">
          <div className="custom-popup">
            <h1 className="popup-title">NOTIFICATION</h1>
            <p className="popup-message">TIRE ROTATION AND BRAKE SERVICE DUE IN</p>
            <div className="popup-distance">900 KM</div>
            <div className="popup-buttons">
              <button className="popup-btn" onClick={handleRemindMe}>REMIND ME</button>
              <button className="popup-btn" onClick={handleServiceDone}>SERVICE DONE</button>
            </div>
          </div>
        </div>
      )}
      <h1>Vehicle Overview</h1>
      <div className="vehicle-list">
        {vehicles.map((vehicle) => (
          <Link key={vehicle.id} to={`/vehicle/${vehicle.id}`} className="vehicle-card-link">
            <div className="vehicle-card">
              <h3>{vehicle.name}</h3>
              <p>Mileage: {vehicle.current_mileage?.toLocaleString()} km</p>
              <p>
                Oil Change Due:{' '}
                <strong style={{ color: vehicle.oil_change_due ? 'red' : 'green' }}>
                  {vehicle.oil_change_due ? 'Yes' : 'No'}
                </strong>
              </p>
              {/* Add more fields as needed */}
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
